import pytest
import data
import sender_stand_request
from datetime import datetime, timedelta
import allure

def get_order_body(test_data_param):
    current_order_body = data.order_body.copy()
    # подмена значения по ключу из test_data_param
    for key, value in test_data_param.items():
        current_order_body[key] = value
    return current_order_body


def get_order_body_with_missing_parameter(test_data_param):
    current_order_body = data.order_body.copy()
    # удаление параметра согласно переданному значению в test_data_param
    del current_order_body[test_data_param]
    return current_order_body


def get_order_creation_response(test_data_param, is_replace):
    if is_replace:
        with allure.step("Подготовка данных для создания заказа, замена данных в шаблоне"):
            current_order_body = get_order_body(test_data_param)
    else:
        with allure.step("Подготовка данных для создания заказа, удаление параметра из шаблона"):
            current_order_body = get_order_body_with_missing_parameter(test_data_param)
    # создание нового заказа
    with allure.step("Отправка запроса на создание нового заказа"):
        new_order = sender_stand_request.post_new_order(current_order_body)
    # объявление track_number со значением несуществующего номера заказа для случая неуспешного содания заказа
    track_number = -1
    if new_order.status_code == 201:
        with allure.step("Заказ создан, получаем трэк номер"):
            track_number = new_order.json()["track"]
    # поиск созданного заказа по трэк номеру
    return sender_stand_request.get_order_by_track(track_number)


def positive_check_found_order_status(order_data):
    with allure.step("Запрос отправлен, посмотрим код ответа"):
        # проверка, что код ответа 200
        assert order_data.status_code == 200, f"Неверный код ответа, получен {order_data.status_code}"

def negative_check_found_order_status(order_data):
    with allure.step("Запрос отправлен, посмотрим код ответа"):
        # проверка, что код ответа 400
        assert order_data.status_code == 400, f"Неверный код ответа, получен {order_data.status_code}"


